const InstallmentPayments = () => {
  return (
    <>
      <BgVideo />
      <StarSky />
      <NewVersionHeader />
      <Container
        maxW={{
          // xs: 345,
          sm: 666,
          md: 900,
          lg: 1200,
        }}
        px='0'
        color={'white'}
        position={'relative'}
        zIndex={2}>
        {/* title */}
        <Flex
          direction={'column'}
          pt='60px'
          align={'center'}>
          <Flex
            columnGap={'20px'}
            flexWrap={'wrap'}
            justify={'center'}>
            <ColorShadow title='Lend' />
            <ColorShadow title='Now' />
            <ColorShadow title='to' />
            <ColorShadow title='expand' />
            <ColorShadow title='community' />
          </Flex>
          <Box
            pt='40px'
            pb='60px'>
            <Text
              fontSize={'20px'}
              color={'#E0E0E0'}
              textAlign={'center'}>
              Your purchase, your pace: Split payments for NFTs over weeks or
              months with ease.
            </Text>
            <Box
              display={'flex'}
              alignItems={'center'}
              justifyContent={'center'}
              position={'relative'}
              zIndex={2}>
              <Box
                // as='button'
                className='home-btn-launch'
                marginX='10px'
                mt='32px'>
                <Icon
                  left={'14px'}
                  top={'6px'}
                  zIndex={3}
                />
                <Icon
                  bottom={'9px'}
                  right={'10px'}
                  zIndex={3}
                  transform={'rotate(180deg)'}
                />
                <Box className='home-btn-bg' />
                <Box
                  className='home-btn-launch-inner'
                  as='button'>
                  Lend Now
                </Box>
              </Box>
            </Box>
          </Box>
        </Flex>

        <Box
          position={'relative'}
          overflow={'hidden'}
          h={'600px'}
          borderRadius={'24px'}
          border={'1px solid rgba(255, 255, 255, 0.08)'}
          mb='50px'>
          <Img src={ImgVideo} />
          <Box className='video-cover'></Box>
        </Box>
        {/* why */}
        <Box mt='72px'>
          <Text
            fontSize={'48px'}
            fontWeight={'900'}
            textAlign={'center'}
            mb='34px'
            color={'white'}
            fontFamily={'Orbitron'}>
            Why xBank Lending is good for NFT owners
          </Text>

          <Flex
            gap={'24px'}
            justify={'space-between'}>
            {WHY1_DATA.map((item) => (
              <WaveCard
                data={{ ...item }}
                key={item.title}
                gap={'20px'}
              />
            ))}
          </Flex>

          <SimpleGrid
            columns={[2]}
            spacing='24px'
            mt='24px'>
            {WHY2_DATA.map((i) => (
              <IconCard
                data={{
                  ...i,
                }}
                key={i.title}
              />
            ))}
          </SimpleGrid>
        </Box>
        <StepGuide
          mt='104px'
          data={{
            title: 'Launch your NFT on xBank Community',
            steps: [
              {
                title: 'Receiving Project Information',
                description: `Understand the project's needs and pain points`,
              },
              {
                title: 'Assessing Qualifications',
                description:
                  'Evaluate needs and plan promotions for qualifying collections',
              },
              {
                title: 'Drafting Plans',
                description:
                  'Discuss and finalize the marketing plan for the collection',
              },
              {
                title: 'Preparing the Scheme',
                description:
                  'List and launch the collection in blur/Opsensea and pool to the protocol',
              },
              {
                title: 'Official Launch',
                description:
                  'Start the activity and optimize the marketing path using data analysis',
              },
            ],
          }}
        />
        <DiscoverMore />
      </Container>
      <NewVersionFooter />
    </>
  )
}

export default InstallmentPayments
